import React, { Component } from 'react'
import { Formik, Field, Form} from 'formik'
import moment from 'moment/moment'

 
class CommentComponent extends Component {
    constructor(props){
        super(props)
        this.state={
            id: this.props.params.id,
            description: "testa",
            inResponseTo: "0",
            targetDate: moment(new Date()).format('YYYY-MM-DD'),
            username: "instructor 1"
        }
    }
    
    render(){
        //unpacking feature in javascript
        let {description, inResponseTo} = this.state
    
        return(
        <div>
            <h1>Comment</h1>
            <div className="container">
                <Formik
                    initialValues={{description, inResponseTo}}
                >
                    {
                        (props)=>(
                            <Form>
                                <fieldset className="form-group">
                                    <label>Description</label>
                                    <Field className="form-control" type="text" name="description"/>
                                </fieldset>

                                <fieldset className="form-group">
                                    <label>In response to</label>
                                    <Field className="form-control" type="text" name="inResponseTo"/>
                                </fieldset>
                                <button className="btn btn-success" type="submit">Save</button>
                            </Form>
                        )
                    }

                </Formik>
            </div>
        </div>
        )
    }

}


export default CommentComponent
