import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Switch, Link, Routes} from 'react-router-dom'
import withNavigation from './WithNavigation.jsx'
import withParams from './WithParams.jsx'
import AuthenticationService from './AuthenticationService.js'
import AuthenticatedRoute from './AuthenticatedRoute.jsx'
import LoginComponent from './LoginComponent.jsx'
import ListCommentsComponent from './ListCommentsComponent.jsx'
import ErrorComponent from './ErrorComponent.jsx'
import HeaderComponent from './HeaderComponent.jsx'


class MoocApp extends Component{
    render(){
        //components that use withNavigate() allow them to navigate to another component programmatically
        const LoginComponentWithNavigation = withNavigation(LoginComponent);
        //Components using withParams() allow them to get key/value pairs of dynamic params from URL, 
        //for example /name=cs8803
        const WelcomeComponentWithParams = withParams(WelcomeComponent);
        const HeaderComponentWithNavigation = withNavigation(HeaderComponent);
       
        const isUserLoggedIn= AuthenticationService.isUserLoggedIn();
        console.log(isUserLoggedIn);

        return(
            <div className="MoocApp">
                <Router>
                    
                <HeaderComponentWithNavigation/>
                    <Routes>
                        <Route path="/"  element={<LoginComponentWithNavigation/>}/>
                        <Route path="/login" element={<LoginComponentWithNavigation/>}/>
                        <Route path="/welcome/:name" element={
                        <AuthenticatedRoute>
                            <WelcomeComponentWithParams /> 
                        </AuthenticatedRoute>
                        }/>
                        <Route path="/comments" element={
                        <AuthenticatedRoute>
                            <ListCommentsComponent />
                        </AuthenticatedRoute>
                        } />
                        <Route path="/logout" element={
                        <AuthenticatedRoute>
                            <LogoutComponent />
                        </AuthenticatedRoute>
                        } />
                        <Route path="*" element={<ErrorComponent />} />
                      </Routes>
                      <FooterComponent/>
                </Router>
                
                {/* <LoginComponent/>
                <WelcomeComponent/> */}
            </div>
        )
    }
}



class LogoutComponent extends Component {
    render() {
        return (
            <>
                <h1>You are logged out</h1>
                <div className="container">
                    Thank You for Using Our Application.
                </div>
            </>
        )
    }
}

class FooterComponent extends Component {
    render() {
        return (
            <footer className="footer">
                <span className="text-muted">CS 8803 - Wei Jin </span>
            </footer>
        )
    }
}






class WelcomeComponent extends Component{
    render() {
        return (
            <>
                <h1>Welcome!</h1>
                <div className="container">
                Welcome {this.props.params.name}. View all instructor comments <Link to="/comments">here</Link>.
                </div>
            </>
            
        )        
    }
}




