import React, {Component} from 'react'
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom'
import withNavigation from './WithNavigation'


class MoocApp extends Component{
    render(){
        const LoginComponentWithNavigation = withNavigation(LoginComponent);
        return(
            <div className="MoocApp">
                <Router>
                    
                        {/* <Routes>
                    <Route path="/" exact component={<LoginComponent/>}/>
                    <Route path="/login" component={<LoginComponent/>}/>
                    <Route path="/welcome" component={<WelcomeComponent/>}/>
                    
                    </Routes> */}
                    <Routes>
                        <Route path="/"  element={<LoginComponentWithNavigation/>}/>
                        <Route path="/login" element={<LoginComponentWithNavigation/>}/>
                        <Route path="/welcome" element={<WelcomeComponent />}/>
                       
                      </Routes>
                </Router>
                {/* <LoginComponent/>
                <WelcomeComponent/> */}
            </div>
        )
    }
}



class WelcomeComponent extends Component{
    render(){
        return <div>Welcome Wei Jin!!</div>
    }
}


class LoginComponent extends Component {
    constructor(props){
        super(props)
        this.state={
            username: 'cs8803',
            password:'',
            hasLoginFailed: false,
            showSuccessMessage:false
        }

        // this.handleUsernameChange = this.handleUsernameChange.bind(this)
        // this.handlePasswordChange = this.handlePasswordChange.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked=this.loginClicked.bind(this)
        
    }


    loginClicked(event){

        if(this.state.username==='cs8803' && this.state.password==='weijin'){
           
            this.props.history.push("/welcome")
            // console.log('Successful')
            // this.setState({showSuccessMessage:true})
            // this.setState({hasLoginFailed:false})
            
            
        }
        else{
            //  console.log('Failed') 
            this.setState({showSuccessMessage:false})
            this.setState({hasLoginFailed:true})
        }
        console.log(this.state);
    }

    handleChange(event){
        //console.log(this.state);
        this.setState(
            {
                [event.target.name]:event.target.value
            }
        )    
    }
    // handlePasswordChange(event){
    //     console.log(event.target.value);
    //     this.setState({password :event.target.value})    
    // }
    render(){
        return(
            <div>
                {/* <ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed} /> */}
                {this.state.hasLoginFailed && <div>Invalid Credentials</div>}
               {/* <ShowLoginSuccessMessage showSuccessMesage={this.state.showSuccessMessage}/> */}
               {this.state.showSuccessMessage&& <div>Login Succesful</div>}
                User Name:<input type ="text" name ="username" value={this.state.username} onChange={this.handleChange}/>
                Password:<input type ="password" name ="password" value={this.state.password} onChange={this.handleChange}/>
                <button onClick={this.loginClicked}>Login</button>
            </div>
        )
    }


    
}

// function ShowInvalidCredentials(props){
//     if(props.hasLoginFailed){
//         return <div>Invalid Credentials</div>    
//     }
//     return null
// }

// function ShowLoginSuccessMessage(props){
//     if(props.showSuccessMesage){
//         return <div>Login Successful</div>
//     }
//     return null 
// }

export default MoocApp
